mapper.select:
select articleno, userid, subject, content, hit, date_format(now(), '%Y/%m/%d') regtime
from board


		where subject like concat('%', #{word}, '%')


		where ${key} = #{word}


order by articleno desc
limit #{start}, #{spp}

select count(articleno) from board
where subject like concat('%', #{word}, '%')
where ${key} = #{word}

select articleno, userid, subject, content, hit, regtime
from board
where articleno = #{articleno}

mapper.update:
update board
set hit = hit + 1
where articleno = #{articleno}

update board
set subject = #{subject}, content = #{content}
where articleno = #{articleno}

update board
set articleno = articleno - 1
where articleno = #{articleno}
